import React from 'react';

class App extends React.Component {
    constructor(props) {
        //super(props);
        this.state = {
            selectedFile: null,
        };
    }

    handleSelectFileButton = () => {
        const fileInput = document.getElementById('fileInput');

        fileInput.click(); // Trigger the file input's click event
    };

    handleFileInputChange = () => {
        const fileInput = document.getElementById('fileInput');
        const selectedFile = fileInput.files[0];

        if (selectedFile) {
            const fileName = selectedFile.name;
            const fileExtension = fileName.split('.').pop().toLowerCase();

            if (fileExtension === 'shp' || fileExtension === 'geojson' || fileExtension === 'kml') {
                this.setState({ selectedFile });
                // You can now perform further actions with the selected file.

                // Optionally, disable the import button
                const uploadButton = document.getElementById('Select-File-Button');
                uploadButton.disabled = true;
            } else {
                alert('Please select a valid SHP, GeoJSON, or KML file.');
            }
        } else {
            // User canceled the file selection or clicked the "Cancel" button
            this.setState({ selectedFile: null });

            // Optionally, re-enable the import button
            const uploadButton = document.getElementById('Select-File-Button');
            uploadButton.disabled = false;
        }
    };

    handleCancelClick = () => {
        const fileInput = document.getElementById('fileInput');
        fileInput.value = ''; // Clear the file input
        this.handleFileInputChange(); // Trigger the file input change event
    };

    render() {
        return (
            <div id="root">
                <div id = "TitleBox">  
                <h1>
                    Map
                </h1> 
                </div>
                <button id="Select-File-Button"
                        onClick={this.handleSelectFileButton}>
                    Select File
                </button>
                <div id = "Container">
                </div>
            </div>
        );
    }


