import 'leaflet/dist/leaflet.css'; // Import Leaflet CSS
import 'leaflet-omnivore'; // Import Leaflet Omnivore
import L from 'leaflet'; // Import Leaflet
import React from 'react';
import './App.css';
import 'file';
class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedFile: null,
        };
    }

    handleSelectFileButton = () => {
        const fileInput = document.getElementById('fileInput');
        fileInput.click();
    };

    handleFileInputChange = () => {
        const fileInput = document.getElementById('fileInput');
        const selectedFile = fileInput.files[0];

        // if (selectedFile) {
        //     const fileName = selectedFile.name;
        //     const fileExtension = fileName.split('.').pop().toLowerCase();

        //     if (fileExtension === 'shp' || fileExtension === 'json' || fileExtension === 'kml') {
        //         this.setState({ selectedFile });
        //         const uploadButton = document.getElementById('Select-File-Button');
        //         uploadButton.disabled = true;
        //     } else {
        //         alert('Please select a valid SHP, GeoJSON, or KML file.');
        //     }
        // }
        if (selectedFile) {
            const fileName = selectedFile.name;
            const fileExtension = fileName.split('.').pop().toLowerCase();

            if (fileExtension === 'shp') {
                this.setState({ selectedFile });
                const uploadButton = document.getElementById('Select-File-Button');
                uploadButton.disabled = true;

                // Load and display the map when the file extension is "shp"
                this.loadShpMap(selectedFile);
            } else if (fileExtension === 'json') {
                // Handle GeoJSON case
            } else if (fileExtension === 'kml') {
                // Handle KML case
            } else {
                alert('Please select a valid SHP, GeoJSON, or KML file.');
            }
        } else {
            this.setState({ selectedFile: null });
            const uploadButton = document.getElementById('Select-File-Button');
            uploadButton.disabled = false;
        }
    };

    handleCancelClick = () => {
        const fileInput = document.getElementById('fileInput');
        fileInput.value = '';
        this.handleFileInputChange();
    };

    loadShpMap = (shpFile) => {
        // Create a Leaflet map
        const map = L.map('Container').setView([0, 0], 10); // Set initial view
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
        }).addTo(map);

        // You can use libraries like leaflet-omnivore to load Shapefiles
        // For example, you can use omnivore to load a Shapefile
        L.leafletOmnivore.shp(USA_roads.shp).addTo(map);
    };

    render() {
        // let Map;
        // if (this.state.selectedFile) {
        //     const fileName = selectedFile.name;
        //     const fileExtension = fileName.split('.').pop().toLowerCase();
        //     if (fileExtension === 'shp') {
        //         Map = (
        //             <div>
        //                 {/* You can customize the map container, map settings, and styles here */}
        //                 <div id="shpMap" style={{ width: '100%', height: '400px' }}></div>
        //             </div>
        //         );
        //     } else if(fileExtension === 'json'){
        //         Map = 
        //     }
        //     else if(fileExtension === 'kml'){
        //         Map = 
        //     }
        //     else{
        //         Map = ""
        //     }
        // }
        // else{
        //     Map = ""
        // }
        return (
            <div id="root">
                <div id="TitleBox">
                    <h1>Map</h1>
                </div>
                <input
                    type="file"
                    id="fileInput"
                    style={{ display: 'none' }}
                    accept=".shp, .json, .kml"
                    onChange={this.handleFileInputChange}
                />
                <button
                    id="Select-File-Button"
                    onClick={this.handleSelectFileButton}
                    disabled={!!this.state.selectedFile}
                >
                    Select File
                </button>
                {this.state.selectedFile && (
                    <div>
                        <p>Selected File: {this.state.selectedFile.name}</p>
                        <button onClick={this.handleCancelClick}>Cancel</button>
                    </div>
                )}
                <div id="Container"></div>
            </div>
        );
    }

}

export default App;
