import 'leaflet/dist/leaflet.css'; // Import Leaflet CSS
import 'leaflet-omnivore'; // Import Leaflet Omnivore
import L from 'leaflet'; // Import Leaflet
import React from 'react';
import './App.css';
import toGeoJSON from 'togeojson';
import { DOMParser } from 'xmldom'; 
import { open } from 'shapefile'; 
import dbf from 'dbf';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedFile: null,
            map: null,
            rendering: false,
            fileExtension: null,
        };
    }

    handleSelectFileButton = () => {
        const fileInput = document.getElementById('fileInput');
        fileInput.accept = '.zip,.shp,.json,.kml';
        fileInput.click();
    };

    handleFileInputChange = () => {
        const fileInput = document.getElementById('fileInput');
        const selectedFile = fileInput.files[0];
        if (selectedFile) {
            const fileName = selectedFile.name;
            const fileExtension = fileName.split('.').pop().toLowerCase();

            if (fileExtension === 'shp' || fileExtension === 'json' || fileExtension === 'kml'){
                this.setState({ selectedFile });
                const uploadButton = document.getElementById('Select-File-Button');
                uploadButton.disabled = true;
                this.loadMap(selectedFile);
                this.setState({ fileExtension: fileExtension })
            } else if (fileExtension === 'zip') {
                this.setState({ selectedFile });
                const uploadButton = document.getElementById('Select-File-Button');
                uploadButton.disabled = true;
                this.loadMap(selectedFile);
                this.setState({ fileExtension: fileExtension })
            }
            else {
                alert('Please select a valid SHP, GeoJSON, or KML file.');
            }
        } else {
            this.setState({ selectedFile: null });
            const uploadButton = document.getElementById('Select-File-Button');
            uploadButton.disabled = false;
        }
    };
    handleRenderButtonClick = () => {
        this.setState({ rendering: true });
        if(this.state.fileExtension==="json"){
            this.renderGeoJSON();
        }
        if(this.state.fileExtension==="kml"){
            this.renderKMLFile();
        }
        if(this.state.fileExtension==="shp"){
            this.renderShpFile();
        }
    };

    handleCancelClick = () => {
        const fileInput = document.getElementById('fileInput');
        fileInput.value = '';
        const container = document.getElementById('Container');
        container.innerHTML = '';

        if(this.state.map){
            this.state.map.remove(); // Remove the old map
            }       
        this.setState({ selectedFile: null, map: null });
        this.handleFileInputChange();
    };

    loadMap = (shpFile) => {
        try {
            // Create a Leaflet map
            const map = L.map('Container').setView([0, 0], 5); // Set initial view
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                attribution: '&copy; < a href=" ">OpenStreetMap</ a> contributors',
            }).addTo(map);
            // Store the new map instance in the component state
            this.setState({ map: map });
        } catch (error) {
            console.error('Error handle loading SHP file:', error);
        }
    };


    renderShpFile = () => {
        const reader = new FileReader();
        if (this.state.map) {
          const map = this.state.map;
          reader.onload = async (e) => {
            try {
              // Parse the SHP file
              const shpData = await open(e.target.result);

              // Read the DBF file as an ArrayBuffer (assuming it's selected as well)
              const dbfDataBuffer = await this.readDbfFileAsArrayBuffer(); // Implement this function to read the DBF file

              // Parse the DBF data using the dbf library
              const dbfData = dbf(dbfDataBuffer);

              // Initialize an empty array to collect GeoJSON features with labels
              const featuresWithLabels = [];

              // Iterate through each feature and add it to the 'featuresWithLabels' array
              while (true) {
                const { done, value } = await shpData.read();
                if (done) break;

                // Use an attribute from the DBF data as the label (replace 'YourLabelField' with the actual field name)
                const featureId = value.id;
                const label = dbfData[featureId].YourLabelField; // Replace 'YourLabelField' with the field name you want to use as labels

                // Add the label as a property to the GeoJSON feature
                value.properties = { label };

                featuresWithLabels.push(value);
              }

              // Create a GeoJSON feature collection
              const geojsonData = {
                type: 'FeatureCollection',
                features: featuresWithLabels,
              };

              // Create a GeoJSON layer and add it to the map
              const geojsonLayer = L.geoJSON(geojsonData, {
                onEachFeature: function (feature, layer) {
                  // Bind the label as a popup to each feature on the map
                  if (feature.properties && feature.properties.label) {
                    layer.bindPopup(feature.properties.label);
                  }
                },
              }).addTo(map);

              // Fit the map bounds to the GeoJSON layer
              map.fitBounds(geojsonLayer.getBounds());
            } catch (error) {
              console.error('Error rendering Shapefile:', error);
            }
          };

          // Read the selected SHP file as an ArrayBuffer
          reader.readAsArrayBuffer(this.state.selectedFile);
        }
    };

      // Function to read the DBF file as an ArrayBuffer
readDbfFileAsArrayBuffer = () => {
    return new Promise((resolve, reject) => {
      const dbfReader = new FileReader();
      dbfReader.onload = (e) => {
        resolve(e.target.result);
      };
      dbfReader.onerror = (error) => {
        reject(error);
      };
      // Assuming this.state.selectedDbfFile contains the selected DBF file
      dbfReader.readAsArrayBuffer(this.state.selectedDbfFile);
    });
};

    renderGeoJSON = () => {
        const reader = new FileReader();
        if (this.state.map) {
            const map = this.state.map;
            reader.onload = (e) => {
                try {
                    const geojsonData = JSON.parse(e.target.result); // Parse as GeoJSON
                    const geojsonLayer = L.geoJSON(geojsonData).addTo(map);

                    // Fit the map bounds to the GeoJSON layer
                    map.fitBounds(geojsonLayer.getBounds());
                }
                catch (error) {
                    console.error('Error rendering GeoJSON:', error);
                }
            }
            // Read the selected file as text
        reader.readAsText(this.state.selectedFile);
        };
    }

    renderKMLFile = () => {
        const reader = new FileReader();
        if (this.state.map) {
            const map = this.state.map;
            reader.onload = (e) => {
            // Read the file content
            const kmlContent = e.target.result;
            // Parse the KML data into a GeoJSON object.
            const geojson = toGeoJSON.kml(new DOMParser().parseFromString(kmlContent, 'text/xml'));
            // Convert KML to GeoJSON using togeojson library
            const geojsonLayer = L.geoJSON(geojson).addTo(map);
            // Fit the map bounds to the GeoJSON layer
            map.fitBounds(geojsonLayer.getBounds());
            }
        reader.readAsText(this.state.selectedFile);
        }
    }
    render() {
        return (
            <div id="root">
                <div id="TitleBox">
                    <h1>Map</h1>
                </div>
                <input
                    type="file"
                    id="fileInput"
                    style={{ display: 'none' }}
                    accept=".shp, .json, .kml"
                    onChange={this.handleFileInputChange}
                />
                {(
                <div>
                <button
                    id="Select-File-Button"
                    onClick={this.handleSelectFileButton}
                    disabled={!!this.state.selectedFile}
                >
                    Select File
                </button>
                <button
                    id="Render-File-Button"
                    onClick={this.handleRenderButtonClick}
                    disabled={!this.state.selectedFile}
                >
                    Render
                </button>
                </div>
                )}

                {this.state.selectedFile && (
                    <div>
                        <p>Selected File: {this.state.selectedFile.name}</p >
                        <button onClick={this.handleCancelClick}>Cancel</button>
                    </div>
                )}
                <div id="Container" ></div>
            </div>
        );
    }
}

export default App;
